server {
        listen 443 ssl;
        listen  [::]:443 ssl;
        server_name localhost;

        ssl_certificate /etc/nginx/certs/nginx_fkey.crt;
        ssl_certificate_key /etc/nginx/certs/nginx_fkey.key;

        location / {
            root   /usr/share/nginx/html;
            index  index.html index.htm;
        }
